#include <OneWire.h>
#include <DallasTemperature.h>

// Define the data pin for the one-wire bus
#define ONE_WIRE_BUS 4

// Initialize OneWire bus
OneWire oneWire(ONE_WIRE_BUS);

// Initialize Dallas Temperature sensor library
DallasTemperature sensors(&oneWire);

void setup() {
  Serial.begin(9600);
  // Start the sensor
  sensors.begin();
}

void loop() {
  // Request temperatures from all sensors
  sensors.requestTemperatures();

  // Get the number of sensors
  int numSensors = sensors.getDeviceCount();

  // Loop through each sensor
  for (int i = 0; i < numSensors; i++) {
    // Get the address of the sensor
    String sensorAddress = sensors.getAddress(i);

    // Get the temperature of the sensor
    float temperatureC = sensors.getTempCByIndex(i);

    // Print the address and temperature
    Serial.print("Sensor ");
    Serial.print(i);
    Serial.print(": Address: ");
    Serial.println(sensorAddress);
    Serial.print("Temperature: ");
    Serial.print(temperatureC);
    Serial.println("¬∞C");
  }

  delay(2000); // Wait 2 seconds before reading again
}
```

