#include <OneWire.h>
#include <DallasTemperature.h>

// Define the data pin connected to the 1-Wire bus
#define ONE_WIRE_BUS 4

// Initialize OneWire and DallasTemperature objects
OneWire oneWire(ONE_WIRE_BUS);
DallasTemperature sensors(&oneWire);

void setup() {
  Serial.begin(115200);
  // Start the serial communication
  sensors.begin();
}

void loop() {
  // Request temperatures from all sensors
  sensors.requestTemperatures();

  // Get the number of sensors found
  int numSensors = sensors.getDeviceCount();

  // Loop through each sensor and print its address and temperature
  for (int i = 0; i < numSensors; i++) {
    // Get the sensor address
    String address = sensors.getAddress(i);

    // Get the temperature in Celsius
    float temperatureC = sensors.getTempCByIndex(i);

    // Print the sensor address and temperature
    Serial.print("Sensor ");
    Serial.print(i);
    Serial.print(": Address = ");
    Serial.println(address);
    Serial.print("Temperature: ");
    Serial.print(temperatureC);
    Serial.println("°C");
  }

  // Wait for a short time before reading again
  delay(2000);
}
```

