  for (int i = 0; i < 8; i++) {
    Serial.print(address[i], HEX);
    if (i < 7) {
      Serial.print(":");
    }
  }
  Serial.print("  Temperature: ");
  Serial.println(temperature);

  delay(1000);  // Wait 1 second before taking the next reading
}

