```javascript
const { OpenAssistant } = require('@openai/open-assistant');
const express = require('express');
const cors = require('cors');

const app = express();
app.use(cors());
app.use(express.json());

const openAssistant = new OpenAssistant({
  apiKey: process.env.OPENAI_API_KEY,
});

app.post('/webhook', (req, res) => {
  const data = req.body.message.text;

  openAssistant.create({
    prompt: `Responder a la siguiente consulta en Telegram: ${data}`,
    max_tokens: 150,
  })
