        reply_to_message_id: req.body.message.message_id,
        text: reply,
      };

      req.telegram.sendMessage(req.body.message.chat.id, JSON.stringify(message));
      res.send({ ok: true });
    })
    .catch((err) => {
      console.error(err);
      res.send({ ok: false });
    });
});

const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`Servidor corriendo en el puerto ${PORT}`);
});
```

